sqlMap.insert:
/* dpl1000DAO.insertDpl1400DeployJobBuildLogInfo - 빌드순번 발급 */ SELECT NVL(MAX(BLD_SEQ)+1,1) AS NEW_BLD_SEQ FROM DPL1400 A WHERE 1=1 AND A.DPL_ID = #dplId# AND A.JEN_ID = #jenId# AND A.JOB_ID = #jobId#
/* dpl1000DAO.insertDpl1400DeployJobBuildLogInfo - 배포 실행 이력 등록 */ INSERT INTO DPL1400 ( DPL_ID ,JEN_ID ,JOB_ID ,BLD_SEQ ,BLD_NUM ,BLD_MAIN_NUM ,BLD_RESULT ,BLD_DURATION_TM ,BLD_START_DTM ,BLD_CONSOLE_LOG ,BLD_RESULT_MSG ,REG_DTM ,REG_USR_ID ,REG_USR_IP ,BLD_RESULT_CD
) VALUES ( #dplId# ,#jenId# ,#jobId# ,#bldSeq# ,#bldNum# ,#bldMainNum# ,#bldResult# ,#estimatedDuration# ,#bldStartDtm# ,#bldConsoleLog# ,#bldResultMsg# ,SYSDATE ,#regUsrId# ,#regUsrIp# ,#bldResultCd# )
