sqlMap.insert:
/* dpl1000DAO.insertDpl100DeployVerInfo - 배포 계획 ID 발급 */ SELECT NVL( SUBSTR(NEW_DPL_ID, 1, 11) || LPAD( (TO_NUMBER(SUBSTR(NEW_DPL_ID, 12, 5)) + 1) , 5, '0') , 'DPL' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '00001' ) AS NEW_DPL_ID FROM ( SELECT MAX(DPL_ID) AS NEW_DPL_ID FROM DPL1000 A WHERE 1=1 AND A.CI_ID = #ciId# AND A.TICKET_ID = #ticketId# AND A.DPL_ID LIKE 'DPL' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '%' )
/* dpl1000DAO.insertDpl100DeployVerInfo - 배포 계획 생성 정보 등록 */ INSERT INTO DPL1000 ( CI_ID ,TICKET_ID ,DPL_ID ,DPL_NM ,DPL_VER ,DPL_DT ,DPL_USR_ID ,DPL_DESC ,DPL_STS_CD ,DPL_TYPE_CD ,DPL_AUTO_AFTER_CD ,DPL_AUTO_DTM ,DPL_RESTORE_CD ,DPL_DEL_CD ,REG_DTM ,REG_USR_ID ,REG_USR_IP
) VALUES ( #ciId# ,#ticketId# ,#newDplId# ,#dplNm# ,#dplVer# ,REPLACE(#dplDt#, '-' ,'') ,#dplUsrId# ,#dplDesc# ,#dplStsCd# ,#dplTypeCd# ,#dplAutoAfterCd# ,#dplAutoDtm# ,#dplRestoreCd# ,'02' ,SYSDATE ,#regUsrId# ,#regUsrIp# )

