/* jen1000DAO.selectJen1000JenkinsInfo - Jenkins 정보 조회 */
SELECT *
FROM (
	
) T1
WHERE 1=1
	AND T1.JEN_ID = #jenId#

/* jen1000DAO.selectJen1100JobInfo - Job 정보 조회(단건) */
SELECT *
FROM (
	
) T1
WHERE 1=1
	AND T1.JOB_ID = #jobId#

sqlMap.update:
/* jen1000DAO.deleteJen1102ParameterInfo - 빌드 파라미터 정보 삭제 */ DELETE FROM JEN1102 WHERE 1=1
AND CI_ID = #ciId#
AND JEN_ID = #jenId# AND JOB_ID = #jobId#

sqlMap.delete:
/* jen1000DAO.deleteJen1101CIJobInfo - CI, JENKINS JOB 연결 정보 삭제 */ DELETE FROM JEN1101 WHERE 1=1
AND CI_ID = #ciId#
AND JEN_ID = #jenId# AND JOB_ID = #jobId#
